import { useState } from 'react';
import CustomModal from './components/Modal/CustomModal';

function App() {
  const [show, setShow] = useState(false);

  const toggleModal = () => {
    return setShow(!show);
  };

  return (
    <div className="App">
      <button onClick={toggleModal}> Open Modal</button>
      {show && <CustomModal />}
    </div>
  );
}

export default App;
